import React, {useState} from 'react';
import Input from '../../components/Inpput';
import PageHeader from '../../components/PageHeader';
import Select from '../../components/Select';
import Textarea from '../../components/Textarea';

function TeacherForm(){

    const [scheduleItems, setscheduleItems] = useState([
        {week_day: 0 , from: '8:00 AM',to: '4:00 PM'}
    ]);

    function addNewScheduleItem(){

       setscheduleItems([
           ...scheduleItems,
           { week_day:0, from: '', to:''
           }
       ]);
    }
    return(
        <div>
        <PageHeader title="q incrivel que voce quer dar aula kk"
        description="primeiro passo é preeencher o formulário">
            test
        </PageHeader>
        <main>
        <fieldset>
            <legend>Seus dados</legend>
            <div className="input-block">
                <label htmlFor="name">Nome completo</label>
                <Input type="text" name='name' label='nome'/>
                <div className="input-block">
                <label htmlFor="avatar">Avatar</label>
                <Input type="text" id="avatar" name='avatar'label ='avatar'/>
            </div>
            <div className="input-block">
                <label htmlFor="whatsapp">Whatsapp</label>
                <Input type="text" id="whatsapp"name='whatsapp' label='avatar'/>
                <Textarea name='bio' label='bota o q tu quiser'/>
            </div>
            </div>
        </fieldset>
        <fieldset>
            <legend>Sobre a aula</legend>
            <Select name='subject' 
            label='materia'
            options={[
                {value: 'Artes', label:'Artes'},
                {value: 'fisica', label:'fisica'},
                {value: 'portugues', label:'portugues'},
            ]}
            ></Select>

            <Input name='cost' label='custo da hora aula'></Input>
        </fieldset>
        <fieldset>
            <legend>horarios disponiveis
                <button onClick={addNewScheduleItem}>novo horario</button>
            </legend>
         {scheduleItenms.map(scheduleItenm =>{
             return(   
             <div key={scheduleItenm.week_day} className="schedule-item">
             <Select name="week_day"
             label='dia da semana'
             options={[
                 {value: '0', label:'domingo'},
                 {value: '1', label:'segunda'},
                 {value: '2', label:'terça'},
                 {value: '3', label:'quarta'},
                 {value: '4', label:'quinta'},
                 {value: '5', label:'sexta'},
                 {value: '6', label:'sabado'},
             ]}
             ></Select>
             <Input name="from" label="das" type="time"></Input>
             <Input name="to" label="até" type="time"></Input>
             </div>);
         })}
        </fieldset>
        <footer>
            <button type="button">Salvar os dados</button>
        </footer>
        </main>
        </div>
    )
}

export default TeacherForm;
